// App.js
import React, { useState, useEffect, createContext } from 'react';
// import KanbanBoard from './KanbanBoard';
import KanbanBoard from './components/KanbanBoard/KanbanBoard';
import './App.css';
import DisplayButton from './components/DisplayButton/DisplayButton';

export const KanbanContext = createContext();

const App = () => {
  const [data, setData] = useState({
    tickets: [],
    users: [],
  });
  const [groupingOption, setGroupingOption] = useState('status');
  const [sortingOption, setSortingOption] = useState('priority');
  const [displayOption, setDisplayOption] = useState(null);

  useEffect(() => {
    // Fetch data from the API and setData
    // This is a placeholder, you should replace it with your actual API call
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.quicksell.co/v1/internal/frontend-assignment ');
        const jsonData = await response.json();
        setData(jsonData);
        console.log(jsonData)
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);
 const handleGroupingChange = (value) => {
    setGroupingOption(value);
  };

  const handleOrderingChange = (value) => {
    setSortingOption(value);
  };



  const contextValue = {
    data,
    groupingOption,
    setGroupingOption,
    sortingOption,
    setSortingOption,
    displayOption,
    setDisplayOption
  };

  return (
     <KanbanContext.Provider value={contextValue}>
        <div className="app">
        <header>
          <label htmlFor="display">Display:</label>
          <select
            id="display"
            value={displayOption}
            onChange={(e) => setDisplayOption(e.target.value)}
          >
            <option value="grouping">Grouping</option>
            <option value="ordering">Ordering</option>
          </select>
        </header>

        {displayOption === 'grouping' && (
          <div>
            <h2>Grouping Options</h2>
            <label htmlFor="groupingOption">Select Grouping Option:</label>
            <select
              id="groupingOption"
              value={groupingOption}
              onChange={(e) => handleGroupingChange(e.target.value)}
            >
              <option value="status">Status</option>
              <option value="user">User</option>
              {/* Add more options as needed */}
            </select>
          </div>
        )}

        {displayOption === 'ordering' && (
          <div>
            <h2>Ordering Options</h2>
            <label htmlFor="orderingOption">Select Ordering Option:</label>
            <select
              id="orderingOption"
              value={sortingOption}
              onChange={(e) => handleOrderingChange(e.target.value)}
            >
              <option value="priority">Priority</option>
              <option value="title">Title</option>
              {/* Add more options as needed */}
            </select>
          </div>
        )}

        <KanbanBoard />
      </div>
     </KanbanContext.Provider>
  );
};

export default App;
