// KanbanBoard.js
import React, { useContext } from 'react';
// import KanbanColumn from './KanbanColumn';
import KanbanColumn from '../KanbanColumn/KanbanColumn';
import '../KanbanBoard/KanbanBoard.css'


import { KanbanContext } from '../../App';

const KanbanBoard = () => {
  const { data, groupingOption, sortingOption } = useContext(KanbanContext);
  const { tickets, users } = data;

  console.log(groupingOption)
  // Implement logic to group and sort tickets based on options
  const sortTickets = (ticketsToSort) => {
    switch (sortingOption) {
      case 'status':
        // Group by status
        return tickets.reduce((groupedTickets, ticket) => {
          const status = ticket.status;
          groupedTickets[status] = groupedTickets[status] || [];
          groupedTickets[status].push(ticket);
          return groupedTickets;
        }, {});
      case 'user':
        // Group by user
        return tickets.reduce((groupedTickets, ticket) => {
          const userId = ticket.userId;
          groupedTickets[userId] = groupedTickets[userId] || [];
          groupedTickets[userId].push(ticket);
          return groupedTickets;
        }, {});
      case 'priority':
        // Group by priority
          return ticketsToSort.sort((a, b) => b.priority - a.priority);
      default:
        return ticketsToSort; // Default to an empty object if no valid grouping option
    }
  };

  // Get the grouped tickets based on the current grouping option
  const groupedTickets = groupTickets();

  const todoTickets = tickets.filter((ticket) => ticket.status === 'Todo');
  const inProgressTickets = tickets.filter((ticket) => ticket.status === 'In progress');
  const doneTickets = tickets.filter((ticket) => ticket.status === 'Done');
  return (
    <div className="kanban-board">
      {/* Render columns based on grouped and sorted tickets */}
      {Object.entries(groupedTickets).map(([groupTitle, groupTickets]) => (
        <KanbanColumn key={groupTitle} title={groupTitle} tickets={groupTickets} users={users} />
      ))}
    </div>
  );
};

export default KanbanBoard;
