// App.js
import React, { useState, useEffect, createContext } from 'react';
// import KanbanBoard from './KanbanBoard';
import KanbanBoard from './components/KanbanBoard/KanbanBoard';
import './App.css';
import DisplayButton from './components/DisplayButton/DisplayButton';

export const KanbanContext = createContext();

const App = () => {
  const [data, setData] = useState({
    tickets: [],
    users: [],
  });
  const [groupingOption, setGroupingOption] = useState('status');
  const [sortingOption, setSortingOption] = useState('priority');
  const [displayOption, setDisplayOption] = useState(null); // Default to null (no option selected)
  const [statusOptions, setStatusOptions] = useState(['Todo', 'In Progress', 'Done']);
  const [groupOptions, setGroupOptions] = useState(['Group Option 1', 'Group Option 2', 'Group Option 3']);


  useEffect(() => {
    // Fetch data from the API and setData
    // This is a placeholder, you should replace it with your actual API call
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.quicksell.co/v1/internal/frontend-assignment ');
        const jsonData = await response.json();
        setData(jsonData);
        console.log(jsonData)
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  const handleStatusOptionClick = (statusOption) => {
    // Handle the selected status option
    console.log('Selected Status Option:', statusOption);
  };

  const handleGroupOptionClick = (groupOption) => {
    // Handle the selected group option
    console.log('Selected Group Option:', groupOption);
  };

  const contextValue = {
    data,
    groupingOption,
    setGroupingOption,
    sortingOption,
    setSortingOption,
  };

  return (
     <KanbanContext.Provider value={contextValue}>
       <div className="app">
        <header>
          {/* <button>Display</button> */}
          {/* <DisplayButton/> */}
        </header>
        <KanbanBoard />
      </div>
     </KanbanContext.Provider>
  );
};

export default App;
