// App.js
import React, { useState, useEffect, createContext } from 'react';

import KanbanBoard from './components/KanbanBoard/KanbanBoard';
import './App.css';
import DisplayButton from './components/DisplayButton/DisplayButton';

export const KanbanContext = createContext();

const App = () => {
  const [data, setData] = useState({
    tickets: [],
    users: [],
  });
  const [loading, setLoading] = useState(true); // Added loading state
  const [displayOptionsVisible, setDisplayOptionsVisible] = useState(false);
  const [groupingOption, setGroupingOption] = useState('status');
  const [orderingOption, setOrderingOption] = useState('priority');




  useEffect(() => {
    // Fetch data from the API and setData
    // This is a placeholder, you should replace it with your actual API call
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.quicksell.co/v1/internal/frontend-assignment ');
        const jsonData = await response.json();
        setData(jsonData);
        setLoading(false);

      } catch (error) {
        console.error('Error fetching data:', error);
      }

    };
    console.log('Fetching data from local storage...');
  const savedData = getFromLocalStorage('kanban-board-data');
  console.log('Saved data from local storage:', savedData);

  if (savedData?.data) {
    console.log('Setting data:', savedData.data);
    setData(savedData.data);
  }
  if (savedData?.displayOptions) {
    setDisplayOptionsVisible(savedData.displayOptions.displayOptionsVisible);
    setGroupingOption(savedData.displayOptions.groupingOption);
    setOrderingOption(savedData.displayOptions.orderingOption);
  }



    fetchData();
  }, [getFromLocalStorage]);
const handleDisplayButtonClick = () => {
    setDisplayOptionsVisible(!displayOptionsVisible);
  };

  const handleGroupingOptionChange = (option) => {

    setGroupingOption(option);
  };

  const handleOrderingOptionChange = (option) => {

    setOrderingOption(option);
  };


  const contextValue = {
    data,
    groupingOption,
    setGroupingOption,
    orderingOption,
    setOrderingOption
  };



// ...




// useEffect(() => {
//   // Fetch data from local storage on component mount
//   console.log("Fetching data from local storage...");
//   const savedData = getFromLocalStorage('kanban-board-data');
//   console.log('Saved data from local storage:', savedData);

//   if (savedData?.data) {
//     console.log('Setting data:', savedData.data);
//     setData(savedData.data);
//   }

//   // Set display options
//   if (savedData?.displayOptions) {
//     setDisplayOptionsVisible(savedData.displayOptions.displayOptionsVisible);
//     setGroupingOption(savedData.displayOptions.groupingOption);
//     setOrderingOption(savedData.displayOptions.orderingOption);
//   }
// }, []);



// ...


  return (
     <KanbanContext.Provider value={contextValue}>
       <div className="app">
        <header>
          <div className="display-container">
            <button onClick={handleDisplayButtonClick}>Display</button>
            {displayOptionsVisible && (
              <div className="dropdown-container">
              <div className="dropdown-container-content">
                <h4>Grouping</h4>
                <select
                  value={groupingOption}
                  onChange={(e) => handleGroupingOptionChange(e.target.value)}
                >
                  <option value="status">Status</option>
                  <option value="user">User</option>
                  <option value="priority">Priority</option>
                  {/* Add more options as needed */}
                </select>
              </div>


              <div className="dropdown-container-content">
                <h4>Ordering</h4>
                <select
                  value={orderingOption}
                  onChange={(e) => handleOrderingOptionChange(e.target.value)}
                >
                  <option value="priority">Priority</option>
                  <option value="title">Title</option>
                  {/* Add more options as needed */}
                </select>
              </div>

              </div>
            )}
          </div>
        </header>
        <KanbanBoard />
      </div>
     </KanbanContext.Provider>
  );
};

export default App;
