// App.js
import React, { useState, useEffect, createContext } from 'react';
// import KanbanBoard from './KanbanBoard';
import KanbanBoard from './components/KanbanBoard/KanbanBoard';
import './App.css';

export const KanbanContext = createContext();

const App = () => {
  const [data, setData] = useState({
    tickets: [],
    users: [],
  });
  const [groupingOption, setGroupingOption] = useState('status');
  const [sortingOption, setSortingOption] = useState('priority');
  const [displayOption, setDisplayOption] = useState('group')

  useEffect(() => {
    // Fetch data from the API and setData
    // This is a placeholder, you should replace it with your actual API call
    const fetchData = async () => {
      try {
        const response = await fetch('https://api.quicksell.co/v1/internal/frontend-assignment ');
        const jsonData = await response.json();
        setData(jsonData);
        console.log(jsonData)
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  const contextValue = {
    data,
    groupingOption,
    setGroupingOption,
    sortingOption,
    setSortingOption,
  };

  return (
     <KanbanContext.Provider value={contextValue}>
       <div className="app">
        <header>
          <button onClick={() => setDisplayOption('group')}>Group</button>
          <button onClick={() => setDisplayOption('status')}>Status</button>
        </header>

        {/* Conditional rendering based on the displayOption */}
        {displayOption === 'group' && (
          <div>
            <h2>Group Options</h2>
            <button onClick={(e) => setGroupingOption(e.target.value)}>Group Option 1</button>
            <button onClick={(e) => setGroupingOption(e.target.value) }>Group Option 2</button>
            {/* Add more buttons as needed */}
          </div>
        )}

        {displayOption === 'status' && (
          <div>
            <h2>Status Options</h2>
            <button onClick={(e) => setSortingOption(e.target.value)}>Status Option 1</button>
            <button onClick={(e) => setSortingOption(e.target.value)}>Status Option 2</button>
            {/* Add more buttons as needed */}
          </div>
        )}

        <KanbanBoard />
      </div>
     </KanbanContext.Provider>
  );
};

export default App;
